INSERT INTO `news` (`id`, `image`, `title`, `intro`, `full_text`, `poster`, `tag`, `featured`, `type`, `date`) VALUES
(1, 'assets/img/uploads/news/ay.jpg', 'Bayelsa Governor, Joins Makinde, Others for PDP Unification Rally in Ibadan', 'Diri was received by the State Governor, Engineer Oluseyi Makinde and former Governor of Ekiti State, Dr. Peter Ayo Fayose, at the Ibadan Airport in the morning. Other people at the rally were former Governor of Osun State, Prince Olagunsoye Oyinlola, Chief Olabode George, former Governor of Ekiti, Omooba Dotun Babayemi, Chief Deji Doherty, Tunde Gbadamosi and host of others.', '', 'Admin', 'News', 0, 'News', 1586700690),
(2, 'assets/img/uploads/news/enugu.jpg', 'COVID-19: Enugu Govt. takes proactive steps, approves N320m', 'The Enugu State Executive Council (EXCO) has approved the sum of Three Hundred and Twenty Million Naira (N320m) to prevent and/or tackle the scourge of Coronavirus – COVID-19 in the State in case of any outbreak.', '', 'Admin', 'Health', 1, 'News', 1586700691),
(3, 'assets/img/uploads/news/Aminu_Waziri_Tambuwal_(cropped).jpg', 'Gov. Tambuwal Emerges Chairman, PDP Governors’ Forum ', 'Sokoto state governor, Aminu Waziri Tambuwal has emerged the chairman of the People’s Democratic Party (PDP) Governors forum. He was elected by his colleagues at a meeting Sunday night. A statement by Muhammad Bello, the Special Adviser, Media and Publicity to governor said his emergence was as a result of a unanimous appointment by the PDP governors while in their 88th National Executive Council meeting.', 'Sokoto state governor, Aminu Waziri Tambuwal has emerged the chairman of the People’s Democratic Party (PDP) Governors forum. He was elected by his colleagues at a meeting Sunday night. A statement by Muhammad Bello, the Special Adviser, Media and Publicity to governor said his emergence was as a result of a unanimous appointment by the PDP governors while in their 88th National Executive Council meeting.  By this development, Tambuwal now replaces outgoing Bayelsa state governor, Seriake Dickson, who was absent at the meeting and whose tenure will elapse on February 3.  Tambuwal while accepting the leadership responsibility entrusted in him by the PDP governors, he pledged to run an inclusive administration hence contributing his quota in moving the party forward. Also speaking at the party’s NEC meeting, the Chairman of PDP Board of Trustees (BoT), Senator Walid Jubrin, said that the party will never change its name.   ', 'Admin', 'News', 1, 'News', 1586700692);

